import React, { useEffect, useState } from "react";
import Transaction from "./Transaction";
import Search from "./Search";
import AddTransactionForm from "./AddTransactionForm";

function TransactionsList() {
  const [transactions, setTransactions] = useState([]);
  const [originalTransactions, setOriginalTransactions] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8001/transactions")
      .then((response) => response.json())
      .then((data) => {
        setTransactions(data);
        setOriginalTransactions(data);
      })
      .catch((error) => console.error("Error fetching transactions", error));
  }, []);

  const addTransaction = (newTransaction) => {
    fetch("http://localhost:8001/transactions", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newTransaction),
    })
      .then((response) => response.json())
      .then((data) => {
        setTransactions([...transactions, data]);
        setOriginalTransactions([...originalTransactions, data]);
      })
      .catch((error) => console.error("Error adding transaction", error));
  };

  const filterTransactions = (searchParams) => {
    const filteredTransactions = originalTransactions.filter((transaction) => {
      const descriptionMatch = transaction.description
        .toLowerCase()
        .includes(searchParams.description.toLowerCase());
      const categoryMatch = transaction.category
        .toLowerCase()
        .includes(searchParams.category.toLowerCase());
      const dateMatch = transaction.date
        .toLowerCase()
        .includes(searchParams.date.toLowerCase());
      const amountMatch = transaction.amount
        .toString()
        .toLowerCase()
        .includes(searchParams.amount.toLowerCase());

      return (
        descriptionMatch && categoryMatch && dateMatch && amountMatch
      );
    });

    setTransactions(filteredTransactions);
  };

  return (
    <div>
      <Search filterTransactions={filterTransactions} />
      <AddTransactionForm addTransaction={addTransaction} />
      <table className="ui celled striped padded table">
        <tbody>
          <tr>
            <th>
              <h3 className="ui center aligned header">Date</h3>
            </th>
            <th>
              <h3 className="ui center aligned header">Description</h3>
            </th>
            <th>
              <h3 className="ui center aligned header">Category</h3>
            </th>
            <th>
              <h3 className="ui center aligned header">Amount</h3>
            </th>
          </tr>
          {transactions.map((transaction) => (
            <Transaction key={transaction.id} transaction={transaction} />
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TransactionsList;
